import { Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';


const BookDetails = () => {
  const { asin } = useParams() /*  destrutturiamo asin */
  const [details, setDetails] = useState({})
  console.log(details[0].title) /* occhio che è un array di oggetti quindi sotto devo usare indice per accedere al singolo valore */
