  const [error, setError] = useState('')

  const bookDetails = async () => {
    setLoading(true)
    try {

      const data = await fetch(`https://epibooks.onrender.com/${asin}`);
      const response = await data.json();
      setDetails(response)
      setLoading(false)
    } catch (error) {

      if (error) setError("C'è stato un errore")

    }
  };

