import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import ContactUs from './ContactUs';
import React, {useState} from 'react';


function Navigation({term, setTerm}) {
  const [showContactUs, setShowContactUs] = useState(false);

  return (
    <Navbar style={{backgroundColor: 'grey'}} expand="lg" sticky='top'>
      <Container>
        <Navbar.Brand style={{color: 'white'}} href="#home">EpiBooks</Navbar.Brand>
        <Navbar.Toggle style={{borderColor: 'white'}} aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link style={{color: 'white'}} href="#home">Home</Nav.Link>
            <Nav.Link style={{color: 'white'}} href="#link">Link</Nav.Link>

            <NavDropdown style={{color: 'grey'}} title="Altro" id="basic-nav-dropdown">
              <NavDropdown.Item style={{color: 'black'}} onClick={() => setShowContactUs(true)}>Contattaci</NavDropdown.Item>
                <ContactUs show={showContactUs} onHide={() => setShowContactUs(false)} />
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.2">Action</NavDropdown.Item>
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.3">Action</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.4">Action</NavDropdown.Item>
            </NavDropdown>

            <input 
              style={{border: '2px solid white', borderRadius: '5px', padding: '5px', marginLeft: '10px'}}
              type="text" 
              placeholder='Cerca...' 
              value={term} 
              onChange={(event) => {
                setTerm(event.target.value)
              }}
            />
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
  
}

export default Navigation;
