import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import { Col, Row } from 'react-bootstrap';
import Spinner from 'react-bootstrap/Spinner';
import BooksList from './BooksList';
import NewCommentArea from './NewCommentArea';
import SearchBar from './SearchBar';
import '../styles/main.css';
import MyFooter from './MyFooter';
import MyNav from './Navigation';
import Welcome from './Welcome';

function Main({ term }) {
  const [booksList, setBooksList] = useState([]);
  const [totalBooksList, setTotalBooksList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selected, setSelected] = useState('');

  const filterBooks = () => {
    if (term !== '') {
      const filteredBooks = totalBooksList.filter((book) =>
        book.title.toLowerCase().includes(term.toLowerCase())
      );
      setBooksList(filteredBooks);
    } else {
      setBooksList(totalBooksList);
    }
  };

  const getBooks = async () => {
    setLoading(true);
    try {
      const response = await fetch('https://epibooks.onrender.com/');
      const data = await response.json();
      setBooksList(data);
      setTotalBooksList(data);
      setLoading(false);
    } catch (error) {
      setError('Errore nella ricezione dei dati');
    }
  };

  useEffect(() => {
    if (term === '') {
      getBooks();
    } else {
      filterBooks();
    }
  }, [term]);

  return (
    <>
      <MyNav term={term} setTerm={setTerm} />
      <Welcome />
      {loading && !error && <Spinner animation="border" />}
      {!loading && !error && (
        <Container className="mt-3 mb-3">
          <Row>
            <Col sm={6} lg={8} xl={9}>
              <BooksList
                booksList={booksList}
                selected={selected}
                setSelected={setSelected}
              />
            </Col>
            <Col sm={6} lg={4} xl={3}>
              {!selected && <div className="commentsContainer"></div>}
              {selected && <NewCommentArea bookId={selected} />}
            </Col>
          </Row>
        </Container>
      )}
      {error && <h2 className="text-danger">{error}</h2>}
      <MyFooter />
    </>
  );
}

export default Main;
