import React from 'react'
import { useParams } from 'react-router-dom'
import { useState, useEffect } from 'react'
import Spinner from 'react-bootstrap/Spinner';
import { Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';


const BookDetails = () => {
  const { asin } = useParams() /*  destrutturiamo asin */
  const [details, setDetails] = useState(null) /* se metto null è meglio di {}, perché il dato potrebbe essere un arraye  non un oggetto */
  console.log(details[0].title) /* occhio che è un array di oggetti quindi sotto devo usare indice per accedere al singolo valore */
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  const bookDetails = async () => {
    setLoading(true)
    try {
      const data = await fetch(`https://epibooks.onrender.com/${asin}`);
      const response = await data.json();
      setDetails(response)
      setLoading(false)
    } catch (error) {
      if (error) setError("C'è stato un errore")
    }
  };

  useEffect (() => {
    bookDetails()
  }, []) 

  return (

    <>
      {/* Hai richiesto il libro {asin} */}

      {loading && <Spinner/>}
      {!loading && error && <div>Errore!</div>}
      {!loading && !error && details && 
      <Card >
        <Card.Img className='object-fit-cover w-25 ' src={details[0].img} />
        <Card.Body>
          <Card.Title className="card-details">{details[0].title}</Card.Title>
          <Card.Text className="card-details"><b>ASIN:</b> {details[0].asin}</Card.Text>
          <Card.Text className="card-details"><b>Categoria:</b> {details[0].category}</Card.Text>
          <Card.Text className="card-details"><b>Prezzo: </b>{`${details[0].price} $`}</Card.Text>
          <Link to={`/`}>  {/* props obbligatoria che ci dice dove deve puntare */}
            <button>Dettagli</button>
          </Link>
        </Card.Body>
      </Card>
      }
    </>
  )
}

export default BookDetails
