import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

const SearchBar = ({ totalBooksList, setBooks }) => {
  const [searchKey, setSearchKey] = useState('');

  const handleSearch = (event) => {
    setSearchKey(event.target.value);
  };

  useEffect(() => {
    const filterBooks = () => {
      if (searchKey !== '') {
        const filteredBooks = totalBooksList.filter((book) =>
          book.title.toLowerCase().includes(searchKey.toLowerCase())
        );
        setBooks(filteredBooks);
      } else {
        setBooks(totalBooksList);
      }
    };

    filterBooks();
  }, [searchKey, totalBooksList, setBooks]);

  return (
    <Container className="mt-3 fs-4">
      <Row sm={12}>
        <input
          type="text"
          placeholder="Search"
          value={searchKey}
          onChange={handleSearch}
        />
      </Row>
    </Container>
  );
};

export default SearchBar;
