import React from 'react'
import { useParams, Link } from 'react-router-dom'
import { useState, useEffect } from 'react'
import Spinner from 'react-bootstrap/Spinner';
import { Card } from 'react-bootstrap';
import Layout from './Layout';


const BookDetails = () => {
  const { asin } = useParams() /*  destrutturiamo asin */
  const [details, setDetails] = useState(null) /*  se non siamo sicuri di cosa arriva, "null" è meglio. potremmo aspettarci un oggetto ma in realtà è un array */
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')

  const bookDetails = async () => {
    setLoading(true)
    try {

      const data = await fetch(`https://epibooks.onrender.com/${asin}`);
      const response = await data.json();
      setDetails(response)
      setLoading(false)
    } catch (error) {
      if (error) setError("C'è stato un errore")
    }
  };

  useEffect(() => {
    bookDetails()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (

    <Layout>
      {loading && <Spinner />}
      {!loading && error && <div>Errore!</div>}
      {!loading && !error && details &&
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
          <Card style={{ width: '23rem' }}>
            <Card.Img variant="top" src={details[0].img} className="object-fit-cover w-100 imageHeight" />
            <Card.Body>
              <Card.Title className="card-details">{details[0].title}</Card.Title>
              <Card.Text className="card-details"><b>ASIN:</b> {details[0].asin}</Card.Text>
              <Card.Text className="card-details"><b>Categoria:</b> {details[0].category}</Card.Text>
              <Card.Text className="card-details"><b>Prezzo:</b> {`${details[0].price} $`}</Card.Text>
              <Link to="/">
                <button style={{ width: '100%', borderRadius: '5px' }}>Torna alla home page</button>
              </Link>
            </Card.Body>
          </Card>
        </div>
      }
    </Layout>
  )
}

export default BookDetails
