import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import ContactUs from './ContactUs';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Navigation({ term, setTerm, setTheme }) {
  const [showContactUs, setShowContactUs] = useState(false);



  /* sono uscito pazzo per fare questo!! */
  return (
    <Navbar expand="lg" sticky='top'>
      <Container>
        <Link style={{ textDecoration: 'none' }} to="/">
          <Navbar.Brand >EpiBooks</Navbar.Brand>
        </Link>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {/* il dropdown non mi cambia di colore */}
            <NavDropdown title="Altro" id="basic-nav-dropdown">
              <NavDropdown.Item onClick={() => setShowContactUs(true)}>Contattaci</NavDropdown.Item>
              <ContactUs show={showContactUs} onHide={() => setShowContactUs(false)} />
              <NavDropdown.Item  href="#action/3.2">Action</NavDropdown.Item>
              <NavDropdown.Item  href="#action/3.3">Action</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item  href="#action/3.4">Action</NavDropdown.Item>
            </NavDropdown>

            <input
              style={{ border: `2px solid`, borderRadius: '5px', padding: '5px', marginLeft: '10px'}}
              type="text"
              placeholder='Cerca...'
              value={term}
              onChange={(event) => {
                setTerm(event.target.value)
              }}
            />

          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navigation;
