import Home from "./pages/Home";
import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import NotFound from "./components/NotFound";
import BookDetails from "./components/BookDetails";

function App() {
  const [query] = useState('')

  return (
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element={<Home term={query} />}/>
        <Route path="/:asin" element={<BookDetails/>}/>
        <Route path="*" element={<NotFound/>}/>   {/*  * è wildcard, cioè tutto ciò che digiti e non corrisponde a rotta specificata, finisce nell'asterisco */}

      </Routes>
    </BrowserRouter>
  );
}

export default App;
