import React, { useContext } from 'react';
import { ThemeContext } from '../Contexts';
import Main from '../components/Main';


function Home() {
  const { theme, query } = useContext(ThemeContext);

  return (
    <div style={{ backgroundColor: '#e2e2e2' }}>
               {/* se tema è light, bg è #f8f9fa, altrimenti bg è white */}
      <Main term={query} />

    </div>
  );
}

export default Home;
