import { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import NotFound from "./components/NotFound";
import BookDetails from "./components/BookDetails";
import { ThemeContext } from "./Contexts"; /* condividere il tema (theme) dell'applicazione tra i componenti */
import Home from "./pages/Home";
import ContactUs from "./pages/ContactUs";

function App() {

  return (
    <BrowserRouter>
      <Routes>
        <Route exact path="/" element=
          {
            <ThemeContext.Provider value={{ query, setQuery }}> {/* condividere il contesto del tema (query e setQuery) con i componenti figli */}
              <Home/>
            </ThemeContext.Provider>
          }
        />
        <Route path="/:asin" element={<BookDetails />} />
        <Route path="/ContactUs" element={<ContactUs></ContactUs>}/>
        <Route path="*" element={<NotFound />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
