import React, { useContext } from 'react';
import Card from 'react-bootstrap/Card';
import '../styles/myCard.css';
import { useState } from 'react';
import { ThemeContext } from '../Contexts';
import { Link } from 'react-router-dom';
import CommentList from './CommentList';

const SingleCard = ({ asin, title, img, price, category, selected, setSelected }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };
  const selectBook = () => {
    setSelected(asin);
    toggleModal();
  };
  // eslint-disable-next-line no-unused-vars
  const { theme } = useContext(ThemeContext);

  return (
    <>
      <Card
        className={`${selected === asin ? 'border border-danger shadow' : null}`}
        key={asin}
        style={{ width: '18rem' }}
        onClick={selectBook}
      >
        <Card.Img className='object-fit-cover w-100 imageHeight' src={img} />
        <Card.Body>
          <Card.Title className="card-details">{title}</Card.Title>
          <Card.Text className="card-details"><b>ASIN:</b> {asin}</Card.Text>
          <Card.Text className="card-details"><b>Categoria:</b> {category}</Card.Text>
          <Card.Text className="card-details"><b>Prezzo: </b>{`${price} $`}</Card.Text>
          <Link to={`/${asin}`}>  {/* props obbligatoria che ci dice dove deve puntare */}
            <button style={{ width: '100%', borderRadius: '5px' }}>Dettagli</button>
          </Link>
        </Card.Body>
      </Card>

      {isModalOpen && selected === asin && (
        <CommentList bookId={asin} />
      )}
    </>
  );
};

export default SingleCard;
