import React, { useState, useEffect } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Spinner from 'react-bootstrap/Spinner';
import BooksList from './BooksList';
import NewCommentArea from './NewCommentArea';
import Navigation from './Navigation';

function Main({ term, setTerm }) {
  const [booksList, setBooksList] = useState([]);
  const [totalBooksList, setTotalBooksList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [selected, setSelected] = useState('');

  const filterBooks = () => {
    if (term !== '') {
      const filteredBooks = totalBooksList.filter((book) =>
        book.title.toLowerCase().includes(term.toLowerCase())
      );
      setBooksList(filteredBooks);
    } else {
      setBooksList(totalBooksList);
    }
  };

  const getBooks = async () => {
    setLoading(true);
    try {
      const response = await fetch('https://epibooks.onrender.com/');
      const data = await response.json();
      setBooksList(data);
      setTotalBooksList(data);
      setLoading(false);
    } catch (error) {
      setError('Errore nella ricezione dei dati');
      setLoading(false);
    }
  };

  useEffect(() => {
    if (term === '') {
      getBooks();
    } else {
      filterBooks();
    }
  }, [term]);

  return (
    <>
      <Navigation term={term} setTerm={setTerm} />
      <Container className="mt-3 mb-3">
        <Row>
          <Col sm={6} lg={8} xl={9}>
            {loading ? (
              <Spinner animation="border" />
            ) : (
              <BooksList booksList={booksList} selected={selected} setSelected={setSelected} />
            )}
          </Col>
          <Col sm={6} lg={4} xl={3}>
            {!selected ? (
              <div className="commentsContainer"></div>
            ) : (
              <NewCommentArea bookId={selected} />
            )}
          </Col>
        </Row>
      </Container>
      {error && <h2 className="text-danger">{error}</h2>}
    </>
  );
}

export default Main;
