import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useContext } from 'react';
import { ThemeContext } from '../Contexts';

function Navigation({term, setTerm, setTheme}) {
  const theme = useContext(ThemeContext)
  return (
    <Navbar style={{backgroundColor: 'pink'}} expand="lg" onClick={setTheme} sticky='top'>
      <Container>
        <Navbar.Brand style={{color: 'white'}} href="#home">EpiBooks</Navbar.Brand>
        <Navbar.Toggle style={{borderColor: 'white'}} aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link style={{color: 'white'}} href="#home">Home</Nav.Link>
            <Nav.Link style={{color: 'white'}} href="#link">Link</Nav.Link>
            <NavDropdown style={{color: 'white'}} title="Altro" id="basic-nav-dropdown">
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.2">Another action</NavDropdown.Item>
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item style={{color: 'black'}} href="#action/3.4">Separated link</NavDropdown.Item>
            </NavDropdown>
            <input 
              style={{border: '2px solid white', borderRadius: '5px', padding: '5px', marginLeft: '10px'}}
              type="text" 
              placeholder='Cerca...' 
              value={term} 
              onChange={(event) => {
                setTerm(event.target.value)
              }}
            />
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
  
}

export default Navigation;
