import React from "react"
import { ListGroup, Button } from "react-bootstrap"
import RatingSystem from "./RatingSystem"
import { useState } from "react"
import Spinner from 'react-bootstrap/Spinner';
import ConfirmWindow from "./ConfirmWindow"

const SingleComment = ({ comment, refreshFunction, title }) => {
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [showConfirmWindow, setShowConfirmWindow] = useState(false);
  const deleteComment = async () => {
    setLoading(true);
    try {
      const data = await fetch(
        `https://striveschool-api.herokuapp.com/api/comments/${comment._id}`,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NDJkN2E4OGIxNGE1MTAwMTQ2NjNmZDIiLCJpYXQiOjE2ODM2NDAyNTMsImV4cCI6MTY4NDg0OTg1M30.RJR7z5DaG1h9smRFnqFXPj1AGNSYMXHCWeHFrZFCjh0",
          },
          method: "DELETE"
        }
      );
      await data.json()
      setLoading(false)
    } catch (error) {
      if (error) setError("Errore nella cancellazione del commento");
    }
  }
  const deleteFunction = async () => {
    await deleteComment()
    await refreshFunction()
    closeConfirmWindow()
  }
  const openConfirmWindow = () => {
    setShowConfirmWindow(true)
  }
  const closeConfirmWindow = () => {
    setShowConfirmWindow(false)
  }
  return (
    <ListGroup.Item className="d-flex justify-content-between align-items-start">
      <div className="ms-2 me-auto">
        <div> <b>Commento:</b> {comment.comment}</div>
          <br></br>
          <div>Titolo: {comment.title}</div>
          <div>
            <Button variant="primary btn-danger" size="sm" onClick={openConfirmWindow}>
              Cancella
            </Button>
          </div>
       
      </div>
      <RatingSystem stars={comment.rate} />
      {loading && !error && <Spinner animation="border" />}
      {error &&
        <div>
          <h5 className="text-danger">{error}</h5>
        </div>
      }
      {showConfirmWindow && (
        <ConfirmWindow 
          question="Sicuro di eliminare il commento ?"
          noFunction={closeConfirmWindow}
          yesFunction={deleteFunction}
        />
      )}
    </ListGroup.Item>
  );
};

export default SingleComment;
