import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import ContactUs from './ContactUs';
import React, { useContext, useState } from 'react';
import { ThemeContext } from '../Contexts';
import { Link } from 'react-router-dom';

function Navigation({ term, setTerm, setTheme }) {
  const [showContactUs, setShowContactUs] = useState(false);
  const { theme } = useContext(ThemeContext);



  /* sono uscito pazzo per fare questo!! */
  return (
    <Navbar style={{backgroundColor: theme === 'light' ? '#cdcdcd' : '#212529' }} expand="lg" sticky='top'>
      <Container>
        <Link style={{ textDecoration: 'none' }} to="/">
          <Navbar.Brand style={{ color: theme === 'light' ? 'black' : 'white' }}>EpiBooks</Navbar.Brand>
        </Link>
        <Navbar.Toggle style={{ borderColor: theme === 'light' ? 'black' : 'white' }} aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            {/* il dropdown non mi cambia di colore */}
            <NavDropdown style={{ color: theme === 'light' ? 'grey' : 'white' }} title="Altro" id="basic-nav-dropdown">
              <NavDropdown.Item style={{ color: theme === 'light' ? 'black' : 'white' }} onClick={() => setShowContactUs(true)}>Contattaci</NavDropdown.Item>
              <ContactUs show={showContactUs} onHide={() => setShowContactUs(false)} />
              <NavDropdown.Item style={{ color: theme === 'light' ? 'black' : 'white' }} href="#action/3.2">Action</NavDropdown.Item>
              <NavDropdown.Item style={{ color: theme === 'light' ? 'black' : 'white' }} href="#action/3.3">Action</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item style={{ color: theme === 'light' ? 'black' : 'white' }} href="#action/3.4">Action</NavDropdown.Item>
            </NavDropdown>

            <input
              style={{ border: `2px solid ${theme === 'light' ? 'black' : 'white'}`, borderRadius: '5px', padding: '5px', marginLeft: '10px', color: theme === 'light' ? 'black' : 'white' }}
              type="text"
              placeholder='Cerca...'
              value={term}
              onChange={(event) => {
                setTerm(event.target.value)
              }}
            />
            <Nav.Link style={{ color: theme === 'light' ? 'black' : 'white' }} onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>Dark Mode</Nav.Link>

          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navigation;
