import React, { useState, useEffect } from "react";
import Spinner from 'react-bootstrap/Spinner';
import NewCommentList from "./NewCommentList";
import "../styles/newCommentArea.css";
import SingleCard from "./SingleCard";

const NewCommentArea = ({ bookId }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [comments, setComments] = useState([]);
  const [selectedCard, setSelectedCard] = useState(null); // Aggiunto stato per la card selezionata

  const getComments = async () => {
    setLoading(true);
    try {
      const data = await fetch(
        `https://striveschool-api.herokuapp.com/api/comments/${bookId}`,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NDJkN2E4OGIxNGE1MTAwMTQ2NjNmZDIiLCJpYXQiOjE2ODM2NDAyNTMsImV4cCI6MTY4NDg0OTg1M30.RJR7z5DaG1h9smRFnqFXPj1AGNSYMXHCWeHFrZFCjh0",
          },
        }
      );
      const response = await data.json();
      setComments(response);
      setLoading(false);
    } catch (error) {
      setError("Errore nella ricezione dei dati");
      setLoading(false);
    }
  };

  useEffect(() => {
    if (bookId !== "") {
      getComments();
    }
  }, [bookId]);

  const handleCardClick = (cardData) => {
    setSelectedCard(cardData);
  };

  return (
    <>
      <SingleCard
        asin={selectedCard?.asin}
        title={selectedCard.title}
        img={selectedCard?.img}
        price={selectedCard?.price}
        category={selectedCard?.category}
        selected={selectedCard?.asin === bookId}
        setSelected={handleCardClick}
      />
      <div className="newCommentAreaContainer">
        {loading && !error && (
          <div className="d-flex justify-content-center">
            <Spinner animation="border" />
          </div>
        )}
        {!loading && !error && (
          <NewCommentList
            bookId={bookId}
            comments={comments}
            refreshFunction={getComments}
          />
        )}
        {error && (
          <div className="alert alert-danger" role="alert">
            <h3 className="text-danger">{error}</h3>
          </div>
        )}
      </div>
    </>
  );
};

export default NewCommentArea;
