import React, { useContext } from 'react'
import Card from 'react-bootstrap/Card'
import '../styles/myCard.css'
import { useState } from 'react'
import { ThemeContext } from '../Contexts'

const SingleCard = ({ asin, title, img, price, category, selected, setSelected }) => {
  const { theme } = useContext(ThemeContext);
  const [isModalOpen, setIsModalOpen] = useState(false)
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen)
  }
  const selectBook = () => {
    setSelected(asin)
    toggleModal()
  }
  return (
    <Card
      className={`${selected === asin ? 'border border-danger shadow' : null}`}
      key={asin}
      style={{ width: '18rem', backgroundColor: theme === 'light' ? 'white' : '#454d55' }}
      onClick={selectBook}
    >
      <Card.Img className='object-fit-cover w-100 imageHeight' src={img} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text><b>ASIN:</b> {asin}</Card.Text>
        <Card.Text><b>Categoria:</b> {category}</Card.Text>
        <Card.Text><b>Prezzo: </b>{`${price} $`}</Card.Text>
      </Card.Body>
    </Card>
  )
}

export default SingleCard
