import Container from 'react-bootstrap/Container'
import { Col, Row } from 'react-bootstrap'
import { useState, useEffect } from 'react'
import Spinner from 'react-bootstrap/Spinner';
import LatestRelease from './LatestRelease'
import NewCommentArea from './CommentArea'
import '../styles/main.css'
import MyFooter from './MyFooter';
import Navigation from './MyNav';
import Welcome from './Welcome';


function Main({ word }) {
  const [booksList, setBooksList] = useState([])
  const [totalBooksList, setTotalBooksList] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState('')
  const [selected, setSelected] = useState('')
  const [query, setQuery] = useState('')

  const filterBooks = () => {
    if (word !== '') {
      setBooksList(totalBooksList.filter((book) =>
        book.title.toLowerCase().includes(word.toLowerCase())))
    } else {
      setBooksList(totalBooksList)
    }
  }
  const getBooks = async () => {
    setLoading(true)
    try {
      const data = await fetch('https://epibooks.onrender.com/')
      const response = await data.json()
      setBooksList(response)
      setTotalBooksList(response)
      setLoading(false)
    } catch (error) {
      if (error) {
        setError('Errore nella ricezione dei dati')
      }
    }
  }

  useEffect(() => {
    if (word === '') {
      getBooks()
    } else {
      filterBooks()
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [word])
  return (
    <>
      <Navigation word={query} setWord={setQuery} />
      <Welcome/>
      {loading && !error && <Spinner animation="border" />}
      {!loading && !error &&
        <Container className='mt-3 mb-3'>
          <Row>
            {/* prima colonna dove c'Ã¨ lista di libri */}
            <Col sm={6} lg={8} xl={9}>
              <LatestRelease booksList={booksList} selected={selected} setSelected={setSelected} />
            </Col>

            {/* seconda colonna dove appaiono commenti */}
            <Col sm={6} lg={4} xl={3}>
              {!selected && <div className='commentsContainer'></div>}
              {selected && <NewCommentArea bookId={selected} />}
            </Col>
          </Row>
        </Container>
      }
      {error && <h2 className='text-danger'>{error}</h2>}
      <MyFooter/>
    </>
  );
}

export default Main;
