import React, { useContext } from "react";
import { Container, Row, Col } from "react-bootstrap";
import SingleCard from "./SingleCard";
import { ThemeContext } from "../Contexts";
import { Link } from "react-router-dom";

const BooksList = ({ booksList, selected, setSelected }) => {
  const { theme } = useContext(ThemeContext);

  return (
    <Container style={{ backgroundColor: theme === 'light' ? '#f8f9fa' : '#212529' }}>
      <Row className="g-2">
        {booksList.map((book) => {
          return (
            <Col key={book.asin} sm={12} lg={6} xl={4}>
              <Link to={`/book-details/${book.asin}`}>
                <SingleCard
                  asin={book.asin}
                  title={book.title}
                  img={book.img}
                  price={book.price}
                  category={book.category}
                  selected={selected}
                  setSelected={setSelected}
                  style={{ backgroundColor: theme === 'light' ? 'white' : '#454d55' }}
                />
              </Link>
            </Col>
          );
        })}
      </Row>
    </Container>
  );
};

export default BooksList;
