import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import ContactUs from './ContactUs';
import React, { useContext, useState } from 'react';
import { ThemeContext } from '../Contexts';
import '../styles/Navigation.css'

function Navigation({ term, setTerm, setTheme }) {
  const [showContactUs, setShowContactUs] = useState(false);
  const { theme } = useContext(ThemeContext);

  /* sono uscito pazzo per fare questo!! */
  return (
    <Navbar style={{ backgroundColor: theme === 'light' ? '#cdcdcd' : '#212529' }} expand="lg" sticky='top'>
      <Container>
        <Navbar.Brand href="#home">EpiBooks</Navbar.Brand>
        <Navbar.Toggle style={{ borderColor: theme === 'light' ? 'black' : 'white' }} aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#home">Home</Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>

            <NavDropdown style={{ color: theme === 'light' ? 'grey' : 'black' }} title="Altro" id="basic-nav-dropdown">
              <NavDropdown.Item className="nav-link" onClick={() => setShowContactUs(true)}>Contattaci</NavDropdown.Item>
              <ContactUs show={showContactUs} onHide={() => setShowContactUs(false)} />
            </NavDropdown>

            <input
              style={{ border: `2px solid ${theme === 'light' ? 'black' : 'white'}`, borderRadius: '5px', padding: '5px', marginLeft: '10px', color: theme === 'light' ? 'black' : 'white' }}
              type="text"
              placeholder='Cerca...'
              value={term}
              onChange={(event) => {
                setTerm(event.target.value)
              }}
            />
            <Nav.Link style={{ color: theme === 'light' ? 'black' : 'white' }} onClick={() => setTheme(theme === 'light' ? 'dark' : 'light')}>Dark Mode</Nav.Link>

          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navigation;
