import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';

const BookDetails = () => {
  const [bookDetails, setBookDetails] = useState(null)
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const { asin } = useParams()

  const getSingleBook = async () => {
    setLoading(true)
    try {
      const data = await fetch(`https://epibooks.onrender.com/${asin}`);
      const response = await data.json();
      setBookDetails(response) //mette nel nostro stato il dato trasformato in json
      setLoading(false)
    } catch (error) {
      if (error) setError("Si è verificato un errore")
    }
  }
  useEffect(()=>{
    getSingleBook()
  },[])
  return (
    <>
    {loading && <div>Caricamento...</div>}
    {!loading && error && <div>C'è un errore</div>}
    {!loading && !error && bookDetails &&
      <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={bookDetails[0].img}/>
      <Card.Body>
        <Card.Title>{bookDetails[0].title}</Card.Title>
        <Card.Text></Card.Text>
        <Link to="/">{/* routing */}
          <Button 
            variant="primary">Torna alla home page
          </Button>
        </Link>
      </Card.Body>
    </Card>
    }
    </>
  )
}

export default BookDetails
